//          Learn React
//        </a>
//      </header>
//    </div>
//  );
//}
class App extends React.Component{
    state = {
        Response: {
            Items: [{
                name: '',
                biography: '',
                id: '',
                powers: []
            }],
            Count: '',
            ScannedCount: ''
        }
    };
    contructor() {
        
    }
   
    componentDidMount() {
        axios.get('/Heroes', { responseType: 'json' }).then(response => { this.setState({ Response: response.data }); });
        //this.callApi()
        //    .then(res => this.setState({ tableData: res.Items}))
        //    .catch(err => console.log(err));
    }
    callApi = async () => {
        const response = await fetch('/Heroes');
        const body = await response.json();
        if (response.status !== 200) throw Error(body.message);
        console.log("Here");
        return body;
    };
    handleSubmit = async e => {
        e.preventDefault();
        const response = await fetch('/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            }
            
        });
        const body = await response.text();
        this.setState({ responseToPost: body });
    };
    render() {
        return (
            <div className="App">
                <table border="2">
                    <thead>
                    <tr>
                            
                                {
                            Object.keys(this.state.Response.Items.entries.keys).map((k, i) => { return (<th>{k}</th>) })
                                }
                            
                        
                        </tr>
                        </thead>
                    <tbody>
                        {
                            Object.keys(this.state.Response.Items).map((k, i) => {
                            let data = this.state.Response.Items[k];
                                return (
                                    <tr key={k.id}>
                                    <td>{k.id}</td>
                                    <td>{data.name}</td>
                                        <td>{data.biography}</td>
                                        <td>
                                            <ul>
                                            {data.powers.map(i => {
                                                return <li>{i}</li>})}
                                            </ul>
                                    </td>



                                </tr>
                                );
                        })}
                    </tbody>
                </table>
                
                
            </div>
        );
    }
}
export default App;
